module("dx.ui.components.blockEditor").
factory("dxClipboardRestriction", ["$window", function dxClipboardRestrictionService(
	$window
) {
	"use strict";
	return function dxClipboardRestriction(blockEditorManagerController, $element, enableExternalPaste) {
		/*
		A strategy for clipboard restriction is used for Chromebook and iOS that sets the clipboard to empty and saves the data intended to be copied into a variable.
		As this is set on every RTF block, this strategy does not work for Windows and Mac OS until we implement single question mode on those platforms.
		Until then, we use an alternative strategy based on clipboard format.
		This alternative strategy can likely be removed once all clients are single question mode.
		*/
		var copiedContent = "";
		var clipboardFormat = "application/digiexam";

		if (enableExternalPaste) {
			clipboardFormat = "text/html";
		}

		$element[0].addEventListener("cut", clipboardCopyCutListener);
		$element[0].addEventListener("copy", clipboardCopyCutListener);
		// Set useCapture to be the first responder of the paste event.
		$element[0].addEventListener("paste", clipboardPasteListener, true);

		function clipboardCopyCutListener(event) {
			// Prevent default because the browser overrides our clipboard content type and
			// because we want to prevent copying of DigiExam content to the outside.
			event.preventDefault();

			if (blockEditorManagerController.activeEditor.scribe) {
				var scribe = blockEditorManagerController.activeEditor.scribe;

				var selection = new scribe.api.Selection();

				// Normalize selection with parent tag to keep formatting.
				var selectedParentTag = selection.range.commonAncestorContainer.parentNode.localName;

				// Edge case that prevents the scribe editor from crashing when pasting a single orphaned list item.
				if (selectedParentTag === "li") {
					selectedParentTag = "p";
				}

				// Edge case that prevents the scribe editor from crashing when pasting multiple orphaned
				// list items by setting the parent tag to ul or ol.
				if (selectedParentTag === "div") {
					selectedParentTag = selection.range.commonAncestorContainer.localName;
				}

				var selectedParentElement = $window.document.createElement(selectedParentTag);
				var documentFragment = selection.range.cloneContents();
				selectedParentElement.appendChild(documentFragment);

				if (selectedParentElement.outerText.length > 0) {
					storeContent(event, selectedParentElement.outerHTML);
				}

				// Scribe has no cut handler, so we delete the selection manually.
				if (event.type === "cut") {
					// The MutationObserver used by Scribe isn't reliable and won't always trigger a
					// transaction on cut and paste. Starting a transaction manually might result in
					// nested transactions but it's better than none.
					scribe.transactionManager.run(function cut() {
						scribe.onCutPaste();
						selection.selection.deleteFromDocument();
					});
				}
			}

			if (blockEditorManagerController.activeEditor.mathquill) {
				var copiedText = "";
				if ($window.getSelection) {
					copiedText = $window.getSelection().toString();
				}

				if (copiedText.length > 0) {
					storeContent(event, copiedText);
				}
			}
		}

		function clipboardPasteListener(event) {
			// Prevent outside content from being pasted into DigiExam.
			// Stop propagation to all other paste listeners (Scribe and Mathquill).
			event.preventDefault();
			event.stopPropagation();

			var digiexamClipboard = event.clipboardData.getData(clipboardFormat);
			var content = digiexamClipboard || copiedContent;

			if (!content) {
				return;
			}

			if (blockEditorManagerController.activeEditor.scribe) {
				var scribe = blockEditorManagerController.activeEditor.scribe;

				// The MutationObserver used by Scribe isn't reliable and won't always trigger a
				// transaction on cut and paste. Starting a transaction manually might result in
				// nested transactions but it's better than none.
				scribe.transactionManager.run(function paste() {
					scribe.onCutPaste();
					scribe.insertHTML(content);
				});
			}

			if (blockEditorManagerController.activeEditor.mathquill) {
				// We do not want HTML tags in our mathquill editor.
				// Therefore we compile the contents of the clipboard to an HTML node.
				var element = $window.document.createElement("p");
				element.innerHTML = content;

				// Extract the text from the node without the HTML tags.
				blockEditorManagerController.activeEditor.mathquill.write(element.outerText);
			}
		}

		function destroy() {
			$element[0].removeEventListener("cut", clipboardCopyCutListener);
			$element[0].removeEventListener("copy", clipboardCopyCutListener);
			$element[0].removeEventListener("paste", clipboardPasteListener, true);
		}

		function storeContent(event, content) {
			if (blockEditorManagerController.onCopyCallback) {
				blockEditorManagerController.onCopyCallback(event);
				copiedContent = content;
			} else {
				event.clipboardData.setData(clipboardFormat, content);
			}
		}

		return {
			destroy: destroy,
		};
	};
}]);

