module("dx.ui.components.froala").
factory("dxReadability", function readabilityService() {
	"use strict";

	var questionState = {};

	function convert(html) {
		var jqHtml = angular.element("<div class='readabilityMode'></div>");
		jqHtml.append(html);

		var selectors = {
			fontFamily: "[style*='font-family']",
			fontSize: "[style*='font-size']",
			color: "[style*='color']",
			background: "[style*='background']",
			backgroundColor: "[style*='background-color']",
			margin: "[style*='margin']",
			fontWeight: "[style*='font-weight']",
		};

		jqHtml.find(selectors.fontFamily).css("font-family", "");
		jqHtml.find(selectors.fontSize).css("font-size", "");
		jqHtml.find(selectors.fontWeight).css("font-weight", "");
		jqHtml.find(selectors.backgroundColor).css("background-color", "");
		jqHtml.find(selectors.background).css("background", "");
		jqHtml.find(selectors.color).css("color", "");
		jqHtml.find(selectors.margin).css("margin", "");

		return jqHtml.prop("outerHTML");
	}

	function getQuestionState(index) {
		return !!questionState[index];
	}

	function toggleQuestionState(index) {
		questionState[index] = !questionState[index];
	}

	return {
		convert: convert,
		toggleQuestionState: toggleQuestionState,
		getQuestionState: getQuestionState,
	};
});

