angular.
module("dx.ui.components.slider").
controller("DxSliderController", ["$scope", "$element", function SliderController(
	$scope,
	$element
) {
	"use strict";

	var self = this;

	var updatesEnabled = true;
	self.displayMarker = self.marker;

	var slider = $element.find(".slider");
	self.sliderColor = "4A4A4A";

	function styleSliderAmount(current) {
		var range = current / self.max;
		slider.attr("style", "background: -webkit-gradient(linear, left top, right top, color-stop(" +
			range +
			", #" + self.sliderColor + "), color-stop(" +
			range +
			", #cccccc))");
	}

	$scope.$watch("vm.marker", function onMarkerChange(newVal, oldVal) {
		if (newVal === oldVal) {
			return;
		}

		if (!updatesEnabled) {
			return;
		}

		slider.value = newVal;
		self.displayMarker = newVal;
		styleSliderAmount(parseFloat(self.marker));
	});

	slider.on("input", function onInput(event) {
		updatesEnabled = false;

		styleSliderAmount(event.target.value);
	});

	slider.on("change", function onChange(event) {
		if (!self.onChange) {
			return;
		}

		self.onChange(parseFloat(event.target.value));
		updatesEnabled = true;
	});

	if (self.options) {
		if (self.options.darkMode) {
			self.sliderColor = "4A90E2";
		}

		switch (self.options.style) {
			case "short" :
				slider.addClass("short");
				break;
			case "flex" :
				slider.addClass("flex");
				break;
		}
	}

	styleSliderAmount(parseFloat(self.marker));
}]);

